mapper.select:
SELECT C.ID, C.MEMBER_ID, CONSULTING_TITLE, CONSULTING_CONTENT, CONSULTING_CATEGORY, MEMBER_NICKNAME, CONSULTING_REGISTER_DATE FROM ( SELECT ROWNUM R, ID, MEMBER_ID, CONSULTING_TITLE, CONSULTING_CONTENT, CONSULTING_CATEGORY, MEMBER_NICKNAME, CONSULTING_REGISTER_DATE FROM ( SELECT ROWNUM D, G.ID, G.MEMBER_ID, CONSULTING_TITLE, CONSULTING_CONTENT, CONSULTING_CATEGORY, MEMBER_NICKNAME, CONSULTING_REGISTER_DATE FROM TBL_MEMBER M JOIN TBL_CONSULTING G ON M.ID = G.MEMBER_ID ORDER BY G.ID DESC )
WHERE ROWNUM <= #{pagination.page} * #{pagination.rowCount} ) C WHERE R > (#{pagination.page} - 1) * #{pagination.rowCount}
AND (CONSULTING_TITLE LIKE'%'||#{search.keyword}||'%' OR CONSULTING_CONTENT LIKE '%'||#{search.keyword}||'%' OR CONSULTING_CATEGORY LIKE '%'||#{search.keyword}||'%')

SELECT C.ID, C.MEMBER_ID, CONSULTING_TITLE, CONSULTING_CONTENT, CONSULTING_CATEGORY, MEMBER_NICKNAME, CONSULTING_REGISTER_DATE
FROM TBL_MEMBER M JOIN TBL_CONSULTING C
ON M.ID = C.MEMBER_ID AND C.ID = #{id}

mapper.update:
UPDATE TBL_CONSULTING
SET CONSULTING_TITLE = #{consultingTitle}, CONSULTING_CONTENT = #{consultingContent}, CONSULTING_CATEGORY = #{consultingCategory}, CONSULTING_UPDATE_DATE = SYSDATE
WHERE ID=#{id}
