mapper.select:
SELECT ID, MEMBER_ID, MEMBER_PASSWORD, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_PHONE_NUMBER, MEMBER_STATUS, MEMBER_GRADE, MEMBER_LOGIN_STATUS, FILE_NAME, FILE_PATH, FILE_UUID, FILE_SIZE, SNS_PROFILE
FROM TBL_MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_PASSWORD = #{memberPassword}
WHERE ID = #{id}

 UPDATE TBL_MEMBER
SET MEMBER_LOGIN_STATUS = 'KAKAO', SNS_PROFILE = #{snsProfile}
WHERE MEMBER_ID = #{memberId}

UPDATE TBL_MEMBER
SET MEMBER_LOGIN_STATUS = 'NAVER', SNS_PROFILE= #{snsProfile}
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT ID, MEMBER_ID, MEMBER_PASSWORD, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_SCHOOL, MEMBER_MAJOR, MEMBER_INTRODUCING, MEMBER_PHONE_NUMBER, MEMBER_STATUS, MEMBER_GRADE, MEMBER_LOGIN_STATUS,  FILE_NAME, FILE_PATH, FILE_UUID, FILE_SIZE, SNS_PROFILE, MEMBER_POINT
FROM TBL_MEMBER
WHERE ID = #{id}

	SELECT ID, MEMBER_ID, MEMBER_PASSWORD, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_PHONE_NUMBER, MEMBER_STATUS, MEMBER_GRADE, MEMBER_LOGIN_STATUS, FILE_PATH, FILE_UUID, FILE_NAME
	FROM TBL_MEMBER
    WHERE ID = #{id}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_SCHOOL=#{memberSchool}, MEMBER_MAJOR=#{memberMajor}, MEMBER_INTRODUCING=#{memberIntroducing}
WHERE id=#{id}

UPDATE TBL_MEMBER
SET FILE_NAME = 'NULL'
WHERE ID=#{id}

UPDATE TBL_MEMBER
SET FILE_NAME = #{fileName}, FILE_PATH = #{filePath}, FILE_UUID = #{fileUuid}, FILE_SIZE= #{fileSize}
WHERE ID=#{id}

mapper.select:
SELECT M.MEMBER_NAME, M.MEMBER_LOGIN_STATUS, M.MEMBER_POINT, M.MEMBER_NICKNAME, M.MEMBER_PHONE_NUMBER
FROM TBL_MEMBER M
WHERE M.ID =#{id}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_STATUS='0'
WHERE ID=#{id}

UPDATE TBL_MEMBER
SET MEMBER_NICKNAME = #{memberNickname}, MEMBER_PHONE_NUMBER = #{memberPhoneNumber}
WHERE ID=#{id}

mapper.select:
SELECT F.FILE_PATH, F.FILE_UUID, F.FILE_NAME
FROM TBL_MEMBER M JOIN TBL_FILE F
ON M.FILE_ID = F.ID AND M.ID = #{id}
